import React  from 'react';
import axios from 'axios'
class Axios extends React.Component {
    //构造函数
    constructor(props) {
        super(props);
        //react定义数据
        this.baseUrl = '/api/goodlist'; // 这里是本地模拟,在public下创建一个api文件,放置一个json文件，这里的路径直接是根路径即可,react会自动的找到这个目录
        // this.baseUrl = 'http://localhost:4000/api/goodlist'; // 这种方式是使用代理的方式,这里用的是mockoon工具
        // this.baseUrl = 'https://easy-mock.com/mock/5b24e12cf076d94c646a9aa2/api/googlists'; //这是使用easy-mock
        this.state = {
            list: []

        }
    }
    //请求接口的方法
    getData=()=>{
        const api='https://www.apiopen.top/weatherApi?city=%E4%B8%8A%E6%B5%B7';
        axios.get(api)
            .then(function (response) {
                // handle success
                console.log(response);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });
    }
    render() {
        return (
            <div>
                <h2>axios获取数据</h2>
                <button onClick={this.getData}>获取api接口</button>
            </div>
        )
    }
}
export default Axios;
